                            <SelectItem value="USD">USD ($) - US Dollar</SelectItem>
                            <SelectItem value="INR">INR (₹) - Indian Rupee</SelectItem>
                            <SelectItem value="EUR">EUR (€) - Euro</SelectItem>
                            <SelectItem value="KWD">KWD (د.ك) - Kuwaiti Dinar</SelectItem>
                          </SelectContent>
                        </Select>
